mapper.select:
SELECT id, author_id, title, introduction, labels, url, classify_id, state, like_num, create_time, update_time FROM drawing
AND author_id = #{authorId}
(title like concat('%', #{title}, '%')
OR labels like concat('%', #{label}, '%'))
AND classify_id = #{classifyId}
AND state = #{state}
LIMIT #{startIndex}, #{pageSize}

SELECT
    count(*)
FROM drawing

    AND author_id = #{authorId}


        (title like concat('%', #{title}, '%')
        OR labels like concat('%', #{label}, '%'))


        AND classify_id = #{classifyId}


        AND state = #{state}

SELECT id, author_id, title, introduction, labels, url, classify_id, state, like_num, create_time, update_time FROM drawing
(title like concat('%', #{title}, '%') OR labels like concat('%', #{label}, '%'))
AND classify_id = #{classifyId}
AND state = #{state}
AND like_num >= #{likeNum}
LIMIT #{startIndex}, #{pageSize}

SELECT
    count(*)
FROM drawing


        (title like concat('%', #{title}, '%')
        OR labels like concat('%', #{label}, '%'))


        AND classify_id = #{classifyId}


        AND state = #{state}


        AND like_num >= #{likeNum}

mapper.update:
UPDATE drawing
SET state = #{state}
WHERE id = #{id}

UPDATE drawing
SET like_num = like_num + #{num}
WHERE id = #{id}
